package it.unipd.threewaymilkshake.portacs.server.collision;

import java.util.Date;
import java.util.TimerTask;
import java.util.concurrent.Executors;
import java.util.concurrent.ScheduledExecutorService;
import java.util.concurrent.TimeUnit;


public class Experiment {

    
    public static void main(String[] args)
        throws InterruptedException {

        Thread repeatedTask1 = new Thread( new Runnable() {
            public void run() {
                System.out.println("Task1 by " + Thread.currentThread().getName().toString() );
        }});

        Thread repeatedTask2 = new Thread( new Runnable() {
            public void run() {
                System.out.println("Task2 by " + Thread.currentThread().getName().toString() );
        }});

        ScheduledExecutorService executor = Executors.newScheduledThreadPool(2);

        executor.scheduleAtFixedRate(repeatedTask1, 1L, 100L, TimeUnit.MILLISECONDS);
        executor.scheduleAtFixedRate(repeatedTask2, 1L, 100L, TimeUnit.MILLISECONDS);

        Thread.sleep(5000L);

        executor.shutdown();
    }
}
